import React from 'react';
import UserProfile from '../UserProfile/UserProfile';

export default class Messages extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            user: {
            }
        }
    }
    componentDidMount() {
        this.setState({user: UserProfile.getUser()});
    }
    render() {
        const listMessages= this.props.lesMessages.map((message) =>
        <div className={`card ${this.state.user.id === message.idExpediteur ? " bg-primary" : ""}`} key={message.id}>
        <div className="card-body">
        {message.message} {message.idExpediteur}
        </div>
        <div class="card-footer text-muted">
        {message.dateEnvoi}
        </div>
        </div>
        );
        return  <div key={this.props.userSelected}>
        <h1>Discussion avec {this.props.userSelected}</h1>
        {listMessages}</div>;
    }
}
