import React from 'react';
import Contacts from './Contacts';
import Messages from './Messages';
import UserProfile from '../UserProfile/UserProfile';

export default class Messagerie extends React.Component {
  constructor(props) {
    super(props)
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      users: [
      ],
      messages: [

      ],
      userSelected: 1,
      user: {
        
      }
    };
  }
  
  componentDidMount() {
    fetch('http://obiwan2.univ-brest.fr:7032/user')
    .then(response => response.json())
    .then(response => this.setState({users: response}))
    .then(response => UserProfile.setUser(this.state.users[1]))
    .catch(err => console.error(err));
    
  }
  refreshMessage(e){
    var id = UserProfile.getId();

    console.log("Mon id : "+id+" Desitinataire id : "+e);
    
    fetch('http://obiwan2.univ-brest.fr:7033/disscussion/'+id+'/'+e)
    .then(response => response.json())
    .then(response => this.setState({messages: response}))
    .catch(err => console.error(err));

  }
  handleClick(e) {
    console.log(e)
    this.setState({userSelected: e});
    this.refreshMessage(e);
    
  }
  render() {
    return <div className="container">
    <div className="row">
    <div className="col-md-3">
    <Contacts users={this.state.users} userSelected={this.state.userSelected} handleClick={this.handleClick} myUser={this.state.user}></Contacts>
    </div>
    <div className="col-md-9">
    <Messages userSelected={this.state.userSelected} lesMessages={this.state.messages} myUser={this.state.user}></Messages>
    </div>
    </div>
    </div>;
  }
}
