import React from 'react';
//import UserProfile from './UserProfile';

export default class Contacts extends React.Component {
    constructor(props) {
        super(props)
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(e) {
        this.props.handleClick(e);
    }
    render() {
        var liste_users = this.props.users;
        // get index of object with id:37
        var removeIndex = liste_users.map(function(item) { return item.id; }).indexOf(this.props.myUser.id);
        console.log(removeIndex)
        // remove object
        liste_users.splice(removeIndex, 1);

        const listUsers= liste_users.map((user) => 
        <a className={`list-group-item list-group-item-action${this.props.userSelected === user.id ? " active" : ""}`} key={user.id} onClick={this.handleClick.bind(this, user.id)}>{user.identifiant}[{user.id}]</a>
        );
        return <div>
        <h3>Mes contacts</h3>
        <div className="list-group">
        {listUsers}
        </div>
        </div>;
    }
}
