import React from 'react';
const burl = "http://obiwan2.univ-brest.fr:7033";
export default class FormMessage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {value: ''};
    }
    sendMessage(valeur) {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(
                { idExpediteur: this.props.myUser.id,
                    idDestinataire:  this.props.userSelected,
                    message: valeur,
                    dateEnvoi: Date(),
                    messageLu: false
                })
            };
            return fetch(burl+'/message', requestOptions)
        }
        
        render() {
            return <form onSubmit={this.sendMessage}>
            <div className="form-group">
            <label>Ecrire un message</label>
            <textarea className="form-control mb-2" id="exampleFormControlTextarea1" rows="3"></textarea>
            <input type="submit" className="btn btn-primary" value="Envoyer"/>
            </div>
            </form>;
        }
    }
