        fetch('http://obiwan2.univ-brest.fr:7033/disscussion/'+id+'/'+this.props.userSelected)
        .then(response => response.json())
        .then(response => this.setState({messages: response}))
        .catch(err => console.error(err));*/
    }
    render() {
        const listMessages= this.props.lesMessages.map((message) =>
        <div className="card" key={message.id}>
        <div className="card-body">
        {message.message}
        </div>
        </div>
        );
        return  <div key={this.props.userSelected}>
            <h1>Discussion avec {this.props.userSelected}</h1>
            {listMessages}</div>;
    }
}
