    }
    sendMessage() {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ identifiant: id, motDePasse:  mdp, idRole: id_Role})
        };
        return fetch(burl+'/message', requestOptions)
    }

    render() {
        return <form>
        <div className="form-group">
        <label>Ecrire un message</label>
        <textarea className="form-control mb-2" id="exampleFormControlTextarea1" rows="3"></textarea>
        <button type="submit" className="btn btn-primary">Envoyer</button>
        </div>
        </form>;
    }
}
